// Simplified scoring formula
const calculateScore = (metrics: SignalMetrics, formation: Formation): number => {
  // Base X algorithm score
  const baseScore = 
    (metrics.replyAuthorEngaged * 75) +
    (metrics.replyGeneral * 13.5) +
    (metrics.retweet * 20) +
    (metrics.like * 10) +
    (metrics.profileClick * 12) +
    (metrics.videoPlayback50 * 8);

  // Penalties
  const penalties = 
    (metrics.negativeReport * -369) +
    (metrics.block * -74) +
    (metrics.mute * -11);

  // Formation multipliers (Strikers, Midfielders, Defenders, Keeper)
  const formationBonus = 
    (formation.strikers * metrics.viralityIndex) +
    (formation.midfielders * metrics.mutualConnectionDensity) +
    (formation.defenders * (1 - metrics.reportRate)) +
    (formation.keeper * metrics.trustScore);

  return (baseScore + penalties) * (1 + formationBonus);
};
